mapper.select:
SELECT SEQ, REGION, CENTER_NAME, ADDRESS, PHONE
FROM Cure_Center
ORDER BY SEQ ASC;

mapper.insert:

    SELECT COALESCE(MAX(seq), 0) + 1 FROM Cure_Center

INSERT INTO Cure_Center
(
SEQ, REGION, CENTER_NAME, ADDRESS, PHONE, X, Y
)
VALUES (
#{seq}, #{region}, #{centerName}, #{address}, #{phone}, #{x}, #{y}
)

mapper.select:
SELECT SEQ, REGION, CENTER_NAME, ADDRESS, PHONE,   XADDRESS, YADDRESS

FROM Cure_Center
WHERE seq = #{seq};

mapper.update:
UPDATE Cure_Center
SET REGION = #{region},
    CENTER_NAME = #{centerName},
    ADDRESS = #{address},
    PHONE = #{phone}

WHERE seq = #{seq};

UPDATE Cure_Center
SET XADDRESS = #{xAddress},
    YADDRESS = #{yAddress}
WHERE ADDRESS = #{address};
